mapper.delete:
delete from trade_bzjtk_zsb
where tdID = #{tdID,jdbcType=VARCHAR}
  and payClass = #{payClass,jdbcType=VARCHAR}

mapper.select:
select tdID, payClass, pjClass, tdName, openDate, reDate, mainAcc, subAcc, cenbkName, 
cusName, cusbkAcc, cusbkName, seqNo, reAmount, rtDate, tseqNo, sseqNo, rtAmount, 
payDate, payAmount, `type`
from trade_bzjtk_zsb
where tdID = #{tdID,jdbcType=VARCHAR}
  and payClass = #{payClass,jdbcType=VARCHAR}

select tdID, payClass, pjClass, tdName, openDate, reDate, mainAcc, subAcc, cenbkName,
cusName, cusbkAcc, cusbkName, seqNo, reAmount, rtDate, tseqNo, sseqNo, rtAmount,
payDate, payAmount, `type`
from trade_bzjtk_zsb
where  `type` = #{type,jdbcType=VARCHAR}

   and rtDate >= #{beginDate,jdbcType=VARCHAR}


   and rtDate <= #{endDate,jdbcType=VARCHAR}

